  const sections: string[] = [];

  // Title
  sections.push(`# ${block.title}`);

  // Description
  if (block.description) {
    sections.push("");
    sections.push(block.description);
  }

  // Notes section
  if (block.notes) {
    sections.push("");
    sections.push("## Notes");
