/**
 * API client for Schedule.md web interface
 */

import type {
  ScheduleBlock,
  ScheduleConfig,
  ScheduleSummary,
  CreateBlockInput,
  EditBlockInput,
  BlockFilter,
  ApiResponse,
} from "../../types";

const API_BASE = "/api";

async function request<T>(
  path: string,
  options: RequestInit = {}
): Promise<T> {
  const response = await fetch(`${API_BASE}${path}`, {
    ...options,
    headers: {
      "Content-Type": "application/json",
      ...options.headers,
    },
  });

  if (!response.ok) {
    const error = await response.text();
    throw new Error(error || `HTTP ${response.status}`);
  }

  return response.json();
}

export const api = {
  // Config
  async getConfig(): Promise<ScheduleConfig> {
    return request<ScheduleConfig>("/config");
  },

  async updateConfig(updates: Partial<ScheduleConfig>): Promise<ScheduleConfig> {
    return request<ScheduleConfig>("/config", {
      method: "PATCH",
      body: JSON.stringify(updates),
    });
  },

  // Blocks
  async listBlocks(filter?: BlockFilter): Promise<ScheduleBlock[]> {
    const params = new URLSearchParams();
    if (filter?.day) params.set("day", filter.day);
    if (filter?.category) params.set("category", filter.category);
    if (filter?.source) params.set("source", filter.source);

    const query = params.toString();
    return request<ScheduleBlock[]>(`/blocks${query ? `?${query}` : ""}`);
  },

  async getBlock(id: string): Promise<ScheduleBlock> {
    return request<ScheduleBlock>(`/blocks/${encodeURIComponent(id)}`);
  },

  async createBlock(input: CreateBlockInput): Promise<ScheduleBlock> {
    return request<ScheduleBlock>("/blocks", {
      method: "POST",
      body: JSON.stringify(input),
    });
  },

  async updateBlock(id: string, updates: EditBlockInput): Promise<ScheduleBlock> {
    return request<ScheduleBlock>(`/blocks/${encodeURIComponent(id)}`, {
      method: "PATCH",
      body: JSON.stringify(updates),
    });
  },

  async deleteBlock(id: string): Promise<void> {
    await request<void>(`/blocks/${encodeURIComponent(id)}`, {
      method: "DELETE",
    });
  },

  // Summary
  async getSummary(): Promise<ScheduleSummary> {
    return request<ScheduleSummary>("/summary");
  },
};

/**
 * WebSocket client for real-time updates
 */
export class ScheduleWebSocket {
  private ws: WebSocket | null = null;
  private reconnectTimer: number | null = null;
  private listeners: Map<string, Set<(data: unknown) => void>> = new Map();

  connect() {
    const protocol = window.location.protocol === "https:" ? "wss:" : "ws:";
    const wsUrl = `${protocol}//${window.location.host}/ws`;

    this.ws = new WebSocket(wsUrl);

    this.ws.onopen = () => {
      console.log("WebSocket connected");
      this.emit("connected", null);
    };

    this.ws.onmessage = (event) => {
      try {
        const message = JSON.parse(event.data);
        this.emit(message.type, message.data);
      } catch (err) {
        console.error("WebSocket message parse error:", err);
      }
    };

    this.ws.onclose = () => {
      console.log("WebSocket disconnected, reconnecting...");
      this.scheduleReconnect();
    };

    this.ws.onerror = (error) => {
      console.error("WebSocket error:", error);
    };
  }

  private scheduleReconnect() {
    if (this.reconnectTimer) return;
    this.reconnectTimer = window.setTimeout(() => {
      this.reconnectTimer = null;
      this.connect();
    }, 2000);
  }

  on(event: string, callback: (data: unknown) => void) {
    if (!this.listeners.has(event)) {
      this.listeners.set(event, new Set());
    }
    this.listeners.get(event)!.add(callback);

    return () => {
      this.listeners.get(event)?.delete(callback);
    };
  }

  private emit(event: string, data: unknown) {
    this.listeners.get(event)?.forEach((cb) => cb(data));
  }

  disconnect() {
    if (this.reconnectTimer) {
      clearTimeout(this.reconnectTimer);
      this.reconnectTimer = null;
    }
    this.ws?.close();
    this.ws = null;
  }
}

export const ws = new ScheduleWebSocket();

