/**
 * DayColumn component - displays a single day in the weekly grid
 */

import React from "react";
import type { ScheduleBlock, ScheduleConfig, DayOfWeek } from "../../types";
import { TimeBlock } from "./TimeBlock";
import { capitalize } from "../../utils/time";

interface DayColumnProps {
  day: DayOfWeek;
  blocks: ScheduleBlock[];
  config: ScheduleConfig;
  hourHeight: number;
  onBlockClick?: (block: ScheduleBlock) => void;
}

export function DayColumn({
  day,
  blocks,
  config,
  hourHeight,
  onBlockClick,
}: DayColumnProps) {
  const { dayStartHour, dayEndHour } = config;
  const totalHours = dayEndHour - dayStartHour;
  const columnHeight = totalHours * hourHeight;

  // Generate hour grid lines
  const hourLines = [];
  for (let i = 0; i <= totalHours; i++) {
    hourLines.push(
      <div
        key={i}
        style={{
          position: "absolute",
          top: `${i * hourHeight}px`,
          left: 0,
          right: 0,
          height: "1px",
          backgroundColor: i === 0 ? "transparent" : "#e5e7eb",
        }}
      />
    );
  }

  // Check if today
  const today = new Date();
  const dayIndex = ["sunday", "monday", "tuesday", "wednesday", "thursday", "friday", "saturday"];
  const isToday = dayIndex[today.getDay()] === day;

  return (
    <div
      className="day-column"
      style={{
        flex: 1,
        minWidth: "120px",
        borderRight: "1px solid #e5e7eb",
        position: "relative",
      }}
    >
      {/* Day header */}
      <div
        style={{
          position: "sticky",
          top: 0,
          backgroundColor: isToday ? "#dbeafe" : "#f9fafb",
          borderBottom: "1px solid #e5e7eb",
          padding: "8px",
          textAlign: "center",
          fontWeight: 600,
          fontSize: "14px",
          color: isToday ? "#1d4ed8" : "#374151",
          zIndex: 5,
        }}
      >
        {capitalize(day)}
        {isToday && (
          <span
            style={{
              display: "inline-block",
              width: "6px",
              height: "6px",
              backgroundColor: "#1d4ed8",
              borderRadius: "50%",
              marginLeft: "6px",
              verticalAlign: "middle",
            }}
          />
        )}
      </div>

      {/* Time grid */}
      <div
        style={{
          position: "relative",
          height: `${columnHeight}px`,
          backgroundColor: isToday ? "#f0f9ff" : "#fff",
        }}
      >
        {hourLines}

        {/* Blocks */}
        {blocks.map((block) => (
          <TimeBlock
            key={block.id}
            block={block}
            config={config}
            dayStartHour={dayStartHour}
            hourHeight={hourHeight}
            onClick={onBlockClick}
          />
        ))}
      </div>
    </div>
  );
}

