const { chromium } = require('playwright');

(async () => {
  const browser = await chromium.launch({ headless: true });
  const page = await browser.newPage();

  // Set a larger viewport to see more content
  await page.setViewportSize({ width: 1400, height: 900 });

  console.log('Loading Ember Studios schedule...');
  await page.goto('https://www.emberstudiosduncan.com/schedule');
  await page.waitForTimeout(6000);

  // Take a screenshot
  await page.screenshot({ path: '/tmp/ember-schedule.png', fullPage: true });
  console.log('Screenshot saved to /tmp/ember-schedule.png');

  // Get accessibility tree
  console.log('\n=== ACCESSIBILITY SNAPSHOT ===\n');
  const snapshot = await page.accessibility.snapshot();

  // Print the tree structure
  function printNode(node, indent = 0) {
    if (!node) return;
    const prefix = '  '.repeat(indent);
    if (node.name && node.name.trim()) {
      console.log(prefix + (node.role || 'node') + ': ' + node.name.substring(0, 100));
    }
    if (node.children) {
      for (const child of node.children) {
        printNode(child, indent + 1);
      }
    }
  }

  printNode(snapshot);

  await browser.close();
})();

