#!/usr/bin/env node
/**
 * Fetch Ember Studios Schedule
 *
 * Usage: node fetch-ember.js [--day YYYY-MM-DD] [--screenshot-only] [--text-only]
 *
 * Outputs:
 *   /tmp/ember-schedule.png  - Screenshot of schedule
 *   /tmp/ember-schedule.txt  - Extracted text
 *   /tmp/ember-schedule.json - Parsed class data (if successful)
 */

const { chromium } = require('playwright');
const fs = require('fs');
const path = require('path');

const CONFIG = {
  url: 'https://www.emberstudiosduncan.com/schedule',
  waitTime: 6000,
  viewport: { width: 1400, height: 1200 },
  outputDir: '/tmp'
};

async function fetchSchedule(options = {}) {
  const browser = await chromium.launch({ headless: true });
  const page = await browser.newPage();

  try {
    await page.setViewportSize(CONFIG.viewport);

    console.log('Navigating to Ember Studios...');
    await page.goto(CONFIG.url);

    console.log(`Waiting ${CONFIG.waitTime/1000}s for content...`);
    await page.waitForTimeout(CONFIG.waitTime);

    const results = { success: true, timestamp: new Date().toISOString() };

    // Screenshot
    if (!options.textOnly) {
      const screenshotPath = path.join(CONFIG.outputDir, 'ember-schedule.png');
      await page.screenshot({ path: screenshotPath, fullPage: true });
      console.log(`Screenshot: ${screenshotPath}`);
      results.screenshot = screenshotPath;
    }

    // Text extraction
    if (!options.screenshotOnly) {
      const text = await page.evaluate(() => document.body.innerText);
      const textPath = path.join(CONFIG.outputDir, 'ember-schedule.txt');
      fs.writeFileSync(textPath, text);
      console.log(`Text: ${textPath}`);
      results.text = textPath;

      // Try to parse classes from text
      const classes = parseClasses(text);
      if (classes.length > 0) {
        const jsonPath = path.join(CONFIG.outputDir, 'ember-schedule.json');
        fs.writeFileSync(jsonPath, JSON.stringify(classes, null, 2));
        console.log(`Parsed ${classes.length} classes: ${jsonPath}`);
        results.classes = classes;
        results.classesPath = jsonPath;
      }
    }

    return results;

  } catch (error) {
    console.error('Fetch failed:', error.message);
    return { success: false, error: error.message };
  } finally {
    await browser.close();
  }
}

function parseClasses(text) {
  const classes = [];
  const lines = text.split('\n');

  let currentDay = null;
  let i = 0;

  while (i < lines.length) {
    const line = lines[i].trim();

    // Detect day header
    const dayMatch = line.match(/Availability for (\w+), (\w+ \d+)/);
    if (dayMatch) {
      currentDay = dayMatch[1].toLowerCase();
    }

    // Detect time (e.g., "7:00 a.m." or "5:30 p.m.")
    const timeMatch = line.match(/^(\d{1,2}:\d{2})\s*(a\.m\.|p\.m\.)/i);
    if (timeMatch && currentDay) {
      const hour = parseInt(timeMatch[1].split(':')[0]);
      const minute = timeMatch[1].split(':')[1];
      const isPM = timeMatch[2].toLowerCase().includes('p');
      const hour24 = isPM && hour !== 12 ? hour + 12 : (!isPM && hour === 12 ? 0 : hour);
      const startTime = `${hour24.toString().padStart(2, '0')}:${minute}`;

      // Next line should be class name
      const className = lines[i + 1]?.trim() || '';
      // Then instructor
      const instructor = lines[i + 2]?.trim() || '';
      // Then duration
      const durationLine = lines[i + 3]?.trim() || '';
      const durationMatch = durationLine.match(/(\d+)\s*(hr|min)/i);

      let endTime = startTime;
      if (durationMatch) {
        const duration = durationMatch[2].toLowerCase() === 'hr'
          ? parseInt(durationMatch[1]) * 60
          : parseInt(durationMatch[1]);
        const endMinutes = hour24 * 60 + parseInt(minute) + duration;
        const endHour = Math.floor(endMinutes / 60) % 24;
        const endMin = endMinutes % 60;
        endTime = `${endHour.toString().padStart(2, '0')}:${endMin.toString().padStart(2, '0')}`;
      }

      // Check availability
      const availabilityLine = lines[i + 4]?.trim() || '';
      const available = !availabilityLine.toLowerCase().includes('closed');

      if (className && !className.match(/^\d/)) {
        classes.push({
          day: currentDay,
          startTime,
          endTime,
          name: className.replace(/[🔥🌤️]/g, '').trim(),
          instructor: instructor,
          available,
          raw: { className, instructor, durationLine, availabilityLine }
        });
      }

      i += 5;
      continue;
    }

    i++;
  }

  return classes;
}

// CLI
const args = process.argv.slice(2);
const options = {
  screenshotOnly: args.includes('--screenshot-only'),
  textOnly: args.includes('--text-only')
};

fetchSchedule(options).then(result => {
  if (result.success) {
    console.log('\nFetch complete!');
  } else {
    console.error('\nFetch failed:', result.error);
    process.exit(1);
  }
});

