      await page.screenshot({ path: '/tmp/ember-after-click.png', fullPage: true });
      fs.writeFileSync('/tmp/ember-after-click.txt', afterText);
    }

  } catch(e) {
    console.log('Click failed:', e.message);
  }

  await browser.close();
  console.log('\\nDone!');
})();
"

