  url: 'https://www.emberstudiosduncan.com/schedule',
  waitTime: 6000,
  navWaitTime: 3000,
  dayWaitTime: 2000,
  viewport: { width: 1400, height: 1200 }
};

// Ensure cache directory exists
if (!fs.existsSync(CACHE_DIR)) {
  fs.mkdirSync(CACHE_DIR, { recursive: true });
}

async function fetchSchedule(options = {}) {
  const browser = await chromium.launch({ headless: true });
  const page = await browser.newPage();
  const results = {
    success: true,
    timestamp: new Date().toISOString(),
    cacheDir: CACHE_DIR,
    days: {}
  };

  try {
    await page.setViewportSize(CONFIG.viewport);

    console.log('Navigating to Ember Studios...');
    await page.goto(CONFIG.url);
    await page.waitForTimeout(CONFIG.waitTime);

    // Get current week info
    const weekInfo = await page.evaluate(() => {
      const text = document.body.innerText;
      const weekMatch = text.match(/Week starting (\w+), (\w+ \d+)/);
      const dayMatch = text.match(/Availability for (\w+), (\w+ \d+)/);
      return {
        week: weekMatch ? weekMatch[0] : null,
        currentDay: dayMatch ? dayMatch[1] : null,
        currentDate: dayMatch ? dayMatch[2] : null
      };
    });
    console.log('Current week:', weekInfo.week);
    results.initialWeek = weekInfo;

    // Navigate to next week if requested
    if (options.nextWeek) {
      console.log('Navigating to next week...');
      await page.click('[data-hook="next-arrow"]');
      await page.waitForTimeout(CONFIG.navWaitTime);

      const newWeekInfo = await page.evaluate(() => {
        const text = document.body.innerText;
        const weekMatch = text.match(/Week starting (\w+), (\w+ \d+)/);
        return weekMatch ? weekMatch[0] : null;
      });
      console.log('Now on:', newWeekInfo);
      results.navigatedToWeek = newWeekInfo;
    }

    // Determine which days to fetch
    let daysToFetch = [];

    if (options.days) {
      daysToFetch = options.days;
    } else if (options.weekDays) {
      // Map weekday names to likely day numbers (approximate)
      const today = new Date();
      const dayOfWeek = today.getDay(); // 0 = Sunday
      const currentWeekStart = today.getDate() - dayOfWeek;

      const dayMap = {
        sun: 0, mon: 1, tue: 2, wed: 3, thu: 4, fri: 5, sat: 6
      };

      daysToFetch = options.weekDays.map(d => {
        const offset = dayMap[d.toLowerCase().substring(0, 3)];
        let dayNum = currentWeekStart + offset;
        if (options.nextWeek) dayNum += 7;
        return dayNum;
      });
    } else if (options.day) {
      daysToFetch = [options.day];
    }

