{
  "name": "network",
  "version": "1.0.0",
  "description": "Regen Network Python MCP - Python-based tools for Regen Network data and analysis",
  "author": {
    "name": "Gaia AI Agent",
    "url": "https://github.com/gaiaaiagent"
  },
  "homepage": "https://github.com/gaiaaiagent/regen-python-mcp",
  "license": "Apache-2.0",
  "keywords": ["regen", "python", "analysis", "data-tools", "mcp"]
}

{
  "name": "registry",
  "version": "1.0.0",
  "description": "Regen Registry Review MCP - Automated document review and analysis for carbon credit projects",
  "author": {
    "name": "Gaia AI Agent",
    "url": "https://github.com/gaiaaiagent"
  },
  "homepage": "https://github.com/gaiaaiagent/regen-registry-review-mcp",
  "license": "Apache-2.0",
  "keywords": ["regen", "registry", "carbon-credits", "document-review", "mcp", "verification"]
}

{
  "mcpServers": {
    "regen-network": {
      "command": "uvx",
      "args": ["regen-python-mcp"],
      "env": {
        "REGEN_MCP_LOG_LEVEL": "INFO"
      },
      "description": "Regen Python MCP Server - Python-based Regen Network tools for ecocredit queries"
    }
  }
}

{
  "mcpServers": {
    "registry-review": {
      "command": "uvx",
      "args": ["registry-review-mcp"],
      "env": {
        "REGISTRY_REVIEW_LLM_EXTRACTION_ENABLED": "true"
      },
      "description": "Registry Review MCP Server - Automated document review for carbon credit projects"
    }
  }
}
