{
  "name": "backlog-plugin",
  "version": "0.1.0",
  "type": "module",
  "description": "Claude Code plugin for Backlog.md task management",
  "private": true,
  "scripts": {
    "mcp": "npx backlog.md mcp start",
    "serve": "npx backlog.md browser",
    "init": "npx backlog.md init"
  },
  "dependencies": {
    "backlog.md": "^1.27.0"
  }
}

{
  "name": "schedule.md",
  "version": "1.0.0",
  "description": "A markdown-native weekly schedule manager",
  "type": "module",
  "main": "src/index.ts",
  "bin": {
    "schedule": "./src/cli.ts"
  },
  "scripts": {
    "dev": "bun --watch run src/cli.ts serve",
    "serve": "bun run src/cli.ts serve",
    "mcp": "bun run src/mcp/server.ts",
    "test": "bun test",
    "typecheck": "tsc --noEmit",
    "fetch-yoga": "bun run skills/yoga-scheduler/scripts/fetch-ember.cjs"
  },
  "dependencies": {
    "@modelcontextprotocol/sdk": "^1.0.0",
    "commander": "^12.0.0",
    "gray-matter": "^4.0.3",
    "zod": "^3.23.0"
  },
  "devDependencies": {
    "@types/bun": "latest",
    "@types/react": "^18.2.0",
    "@types/react-dom": "^18.2.0",
    "playwright": "^1.40.0",
    "react": "^18.2.0",
    "react-dom": "^18.2.0",
    "typescript": "^5.4.0"
  }
}

{
  "name": "agents",
  "version": "0.1.0",
  "description": "Master plugin for building AI agents across frameworks. Provides skills for each agent architecture in resources/agents/, covering multi-agent orchestration, memory systems, tool integration, protocol standards, and production deployment patterns.",
  "author": {
    "name": "linuxiscool"
  },
  "keywords": ["agents", "multi-agent", "orchestration", "memory", "tools", "protocols", "a2a", "mcp"],
  "skills": ["./skills/"]
}

{
  "name": "brainstorm",
  "version": "0.1.0",
  "description": "Brainstorm with the user in an organized way",
  "author": {
    "name": "linuxiscool"
  },
  "keywords": ["brainstorm", "planning", "thinking", "reflecting"]
}

{
  "name": "llms",
  "version": "0.1.0",
  "description": "Master plugin for working with LLMs, embeddings, and AI frameworks. Provides skills for each embedding architecture in resources/embeddings/, embracing a learning journey through progressive mastery of vector databases, knowledge graphs, and LLM integration patterns.",
  "author": {
    "name": "linuxiscool"
  },
  "keywords": ["llms", "embeddings", "vectors", "knowledge-graphs", "rag", "agents", "learning"],
  "skills": ["./skills/"]
}

