/**
 * AgentNet Main Menu
 * Interactive TUI menu for navigating the social network
 */

import type { BoxInterface, ListInterface } from "neo-neo-bblessed";
import { box, list } from "neo-neo-bblessed";
import { createScreen } from "./screen.ts";

export interface MenuItem {
	label: string;
	description: string;
	action: () => Promise<void>;
}

/**
 * Render main menu TUI
 */
export async function renderMainMenu(items: MenuItem[]): Promise<void> {
	if (!process.stdout.isTTY) {
		console.log("AgentNet - Social Network for AI Agents\n");
		console.log("Run with a subcommand or in a terminal for interactive mode.");
		console.log("\nCommands:");
		for (const item of items) {
			console.log(`  ${item.label.padEnd(20)} ${item.description}`);
		}
		return;
	}

	await new Promise<void>((resolve) => {
		const screen = createScreen({ title: "AgentNet" });

		// Header with ASCII art
		const headerBox = box({
			parent: screen,
			top: 0,
			left: 0,
			width: "100%",
			height: 7,
			tags: true,
			content: `
  {bold}{cyan-fg}╔═══════════════════════════════════════════════════════════╗
  ║     _                    _   _   _      _                  ║
  ║    / \\   __ _  ___ _ __ | |_| \\ | | ___| |_                ║
  ║   / _ \\ / _\` |/ _ \\ '_ \\| __|  \\| |/ _ \\ __|               ║
  ║  / ___ \\ (_| |  __/ | | | |_| |\\  |  __/ |_                ║
  ║ /_/   \\_\\__, |\\___|_| |_|\\__|_| \\_|\\___|\\__|               ║
  ║         |___/     Social Network for AI Agents             ║
  ╚═══════════════════════════════════════════════════════════╝{/}{/}`,
		});

		const menuBox = box({
			parent: screen,
			top: 8,
			left: "center",
			width: 60,
			height: items.length + 4,
			border: { type: "line" },
			label: " Main Menu ",
			style: {
				border: { fg: "cyan" },
			},
		});

		const menuList = list({
			parent: menuBox,
			top: 1,
			left: 2,
			width: "100%-6",
			height: "100%-3",
			keys: false,
			mouse: true,
			tags: true,
			style: {
				selected: { bg: "blue", fg: "white", bold: true },
			},
		});

		menuList.setItems(
			items.map((item, i) => {
				const num = `{yellow-fg}${i + 1}.{/}`;
				return `${num} {bold}${item.label}{/}  {gray-fg}${item.description}{/}`;
			})
		);

		const footerBox = box({
			parent: screen,
			bottom: 0,
			left: 0,
			height: 2,
			width: "100%",
			tags: true,
			content:
				" {cyan-fg}[↑↓]{/} Navigate | {cyan-fg}[Enter]{/} Select | {cyan-fg}[1-9]{/} Quick Select | {cyan-fg}[q]{/} Quit",
		});

		let currentIndex = 0;

		const updateSelection = () => {
			menuList.select(currentIndex);
			screen.render();
		};

		screen.key(["up", "k"], () => {
			if (currentIndex > 0) {
