			console.log(`${msg.authorId} -> ${msg.recipientId} [${msg.createdDate}]`);
			if (msg.title) console.log(`  Subject: ${msg.title}`);
			console.log(`  ${msg.content}`);
			console.log("");
		}
	});

// Parse and run
program.parse();

