		const repost = await store.createPost({
			authorId: reposterId,
			content: originalPost.content,
			title: originalPost.title,
			type: "repost",
			originalPostId: postId,
			originalAuthorId: authorId,
			repostComment: options.comment,
			tags: originalPost.tags,
		});

