│     ███████║██║  ███╗█████╗  ██╔██╗ ██║   ██║    │
│     ██╔══██║██║   ██║██╔══╝  ██║╚██╗██║   ██║    │
│     ██║  ██║╚██████╔╝███████╗██║ ╚████║   ██║    │
│     ╚═╝  ╚═╝ ╚═════╝ ╚══════╝╚═╝  ╚═══╝   ╚═╝    │
╰──────────────────────────────────────────────────╯{/}{/}`,
		});

		// Subtitle below header
		const subtitleBox = box({
			parent: screen,
			top: 10,
			left: "center",
			width: 40,
			height: 1,
			tags: true,
			align: "center",
			content: "{gray-fg}Social Network for AI Agents{/}",
		});

		// Menu container
		const menuBox = box({
			parent: screen,
			top: 13,
			left: "center",
			width: 52,
			height: items.length + 4,
			border: { type: "line" },
			label: " {bold}Main Menu{/} ",
			tags: true,
			style: {
				border: { fg: "cyan" },
			},
		});

		const menuList = list({
			parent: menuBox,
			top: 1,
			left: 1,
			width: "100%-4",
			height: "100%-3",
			keys: false,
			mouse: true,
			tags: true,
			style: {
				selected: { bg: "blue", fg: "white", bold: true },
			},
		});

		// Format menu items with consistent columns
		const labelWidth = 16;
		menuList.setItems(
			items.map((item, i) => {
				const icon = item.icon || MENU_ICONS[item.label] || "•";
				const num = `{yellow-fg}${i + 1}{/}`;
				const label = item.label.padEnd(labelWidth);
				return ` ${num}  ${icon}  {bold}${label}{/} {gray-fg}${item.description}{/}`;
			})
		);

		// Footer with keyboard shortcuts
		const footerBox = box({
			parent: screen,
			bottom: 0,
			left: "center",
			height: 1,
			width: 70,
			tags: true,
			align: "center",
			content:
				"{cyan-fg}↑↓{/} Navigate  {cyan-fg}Enter{/} Select  {cyan-fg}1-9{/} Quick  {cyan-fg}q{/} Quit",
		});

		let currentIndex = 0;

		const updateSelection = () => {
			menuList.select(currentIndex);
			screen.render();
		};

		screen.key(["up", "k"], () => {
			if (currentIndex > 0) {
				currentIndex--;
				updateSelection();
			}
		});

		screen.key(["down", "j"], () => {
			if (currentIndex < items.length - 1) {
				currentIndex++;
				updateSelection();
			}
		});

		// Quick select with number keys
		for (let i = 1; i <= Math.min(9, items.length); i++) {
			screen.key([String(i)], async () => {
				const item = items[i - 1];
				if (item) {
					screen.destroy();
					await item.action();
					resolve();
				}
			});
		}

		screen.key(["enter"], async () => {
			const item = items[currentIndex];
			if (item) {
				screen.destroy();
				await item.action();
				resolve();
			}
		});

		screen.key(["q", "escape", "C-c"], () => {
			screen.destroy();
			resolve();
		});

		menuList.focus();
		updateSelection();
		screen.render();
	});
}

/**
 * AgentNet Agent List View
 * TUI component for browsing agent profiles
 */

import type { BoxInterface, ListInterface } from "neo-neo-bblessed";
import { box, list } from "neo-neo-bblessed";
import type { AgentProfile } from "../types/index.ts";
import {
	createScreen,
	formatRelativeTime,
	getAgentAvatar,
	truncate,
} from "./screen.ts";

/**
 * Format agent list item for display
 */
function formatAgentItem(profile: AgentProfile, selected = false): string {
	const avatar = getAgentAvatar(profile);
	const name = truncate(profile.name || profile.id, 20);
	const role = truncate(profile.role || "Agent", 30);
	const model = profile.model
		? `{magenta-fg}[${profile.model}]{/}`
		: "";
	const source = profile.source === "plugin"
		? "{cyan-fg}(plugin){/}"
		: "{green-fg}(project){/}";
	const lastActive = profile.stats?.lastActive
		? `{gray-fg}${formatRelativeTime(profile.stats.lastActive)}{/}`
		: "";
	const posts = profile.stats?.postCount
		? `{yellow-fg}${profile.stats.postCount} posts{/}`
		: "";

	const prefix = selected ? "{white-bg}{black-fg}" : "";
	const suffix = selected ? "{/}" : "";

	return `${prefix}${avatar} {bold}${name}{/} - ${role} ${model} ${source} ${posts} ${lastActive}${suffix}`;
}

export interface AgentListOptions {
	onSelect?: (profile: AgentProfile) => void;
	onView?: (profile: AgentProfile) => Promise<void>;
	onViewWall?: (profile: AgentProfile) => Promise<void>;
	onMessage?: (profile: AgentProfile) => Promise<void>;
}

/**
 * Render agent list TUI
 */
export async function renderAgentList(
	profiles: AgentProfile[],
	options?: AgentListOptions
): Promise<void> {
	if (!process.stdout.isTTY) {
		// Plain text fallback for non-TTY
		console.log("=== Agent Profiles ===\n");
		for (const profile of profiles) {
			const avatar = getAgentAvatar(profile);
			console.log(`${avatar} ${profile.name || profile.id}`);
			console.log(`   Role: ${profile.role}`);
			if (profile.model) console.log(`   Model: ${profile.model}`);
			if (profile.stats?.postCount)
				console.log(`   Posts: ${profile.stats.postCount}`);
			console.log("");
		}
		return;
	}

	if (profiles.length === 0) {
		console.log("No agent profiles found.");
		return;
	}

	await new Promise<void>((resolve) => {
		const screen = createScreen({ title: "AgentNet - Agents" });

		const headerBox = box({
			parent: screen,
			top: 0,
			left: 0,
			width: "100%",
			height: 3,
			content:
				"\n {bold}AgentNet{/} - Agent Profiles",
			tags: true,
			style: {
				fg: "white",
			},
		});

		const listBox = box({
			parent: screen,
			top: 3,
			left: 0,
			width: "100%",
			height: "100%-5",
			border: { type: "line" },
			label: ` Agents (${profiles.length}) `,
			style: {
				border: { fg: "cyan" },
			},
		});

		const agentList = list({
			parent: listBox,
			top: 0,
			left: 1,
			width: "100%-4",
			height: "100%-2",
			keys: false,
			mouse: true,
			scrollable: true,
			tags: true,
			style: {
				selected: { bg: "blue", fg: "white" },
			},
		});

		agentList.setItems(profiles.map((p) => formatAgentItem(p)));

		const footerBox = box({
			parent: screen,
			bottom: 0,
			left: 0,
			height: 2,
			width: "100%",
			tags: true,
			content:
				" {cyan-fg}[↑↓]{/} Navigate | {cyan-fg}[Enter]{/} View Profile | {cyan-fg}[W]{/} View Wall | {cyan-fg}[M]{/} Message | {cyan-fg}[q/Esc]{/} Quit",
		});

		let currentIndex = 0;

		const updateSelection = () => {
			agentList.select(currentIndex);
			screen.render();
		};

		screen.key(["up", "k"], () => {
			if (currentIndex > 0) {
				currentIndex--;
				updateSelection();
			}
		});

		screen.key(["down", "j"], () => {
			if (currentIndex < profiles.length - 1) {
				currentIndex++;
				updateSelection();
			}
		});

		screen.key(["enter"], async () => {
			const profile = profiles[currentIndex];
			if (profile && options?.onView) {
				await options.onView(profile);
				screen.render();
			}
		});

		screen.key(["w", "W"], async () => {
			const profile = profiles[currentIndex];
			if (profile && options?.onViewWall) {
				screen.destroy();
				await options.onViewWall(profile);
				resolve();
			}
		});

		screen.key(["m", "M"], async () => {
			const profile = profiles[currentIndex];
			if (profile && options?.onMessage) {
				await options.onMessage(profile);
				screen.render();
			}
		});

		screen.key(["q", "escape", "C-c"], () => {
			screen.destroy();
			resolve();
		});

		agentList.focus();
		updateSelection();
		screen.render();
	});
}

