/**
 * AgentNet Screen Utilities
 * TUI screen management following Backlog.md patterns
 */

import type { ScreenInterface } from "neo-neo-bblessed";
import { screen } from "neo-neo-bblessed";

export interface ScreenOptions {
	title?: string;
	smartCSR?: boolean;
	fullUnicode?: boolean;
}

/**
 * Create a blessed screen instance
 */
export function createScreen(options?: ScreenOptions): ScreenInterface {
	return screen({
		title: options?.title || "AgentNet",
		smartCSR: options?.smartCSR ?? true,
		fullUnicode: options?.fullUnicode ?? true,
	});
}

/**
 * Format date for display
 */
export function formatDate(date: string | Date): string {
	const d = typeof date === "string" ? new Date(date) : date;
	return d.toLocaleDateString("en-US", {
		month: "short",
		day: "numeric",
		hour: "2-digit",
		minute: "2-digit",
	});
}

/**
 * Format relative time
 */
export function formatRelativeTime(date: string | Date): string {
	const d = typeof date === "string" ? new Date(date) : date;
	const now = new Date();
	const diff = now.getTime() - d.getTime();

	const minutes = Math.floor(diff / 60000);
	const hours = Math.floor(diff / 3600000);
	const days = Math.floor(diff / 86400000);

	if (minutes < 1) return "just now";
	if (minutes < 60) return `${minutes}m ago`;
	if (hours < 24) return `${hours}h ago`;
	if (days < 7) return `${days}d ago`;
	return formatDate(d);
}

/**
 * Truncate text with ellipsis
 */
export function truncate(text: string, maxLength: number): string {
	if (text.length <= maxLength) return text;
	return `${text.slice(0, maxLength - 1)}…`;
}

/**
 * Get emoji avatar for agent based on model or role
 */
export function getAgentAvatar(profile: {
	avatar?: string;
	model?: string;
	role?: string;
}): string {
	if (profile.avatar) return profile.avatar;

	// Default avatars based on model
	if (profile.model) {
		switch (profile.model.toLowerCase()) {
			case "opus":
				return "🎭";
			case "sonnet":
				return "🎵";
			case "haiku":
				return "🌸";
		}
	}

	// Default avatars based on role keywords
	const role = (profile.role || "").toLowerCase();
	if (role.includes("architect")) return "🏛️";
	if (role.includes("thinker") || role.includes("systems")) return "🧠";
	if (role.includes("archivist") || role.includes("archive")) return "📚";
	if (role.includes("librarian")) return "📖";
	if (role.includes("validator")) return "✅";
	if (role.includes("mentor")) return "🎓";
	if (role.includes("cartographer") || role.includes("map")) return "🗺️";
	if (role.includes("navigator")) return "🧭";
	if (role.includes("explorer")) return "🔭";

	return "🤖";
}

/**
 * Get status indicator for post type
 */
export function getPostTypeIcon(type: string): string {
	switch (type) {
		case "original":
			return "📝";
		case "repost":
			return "🔄";
		case "reply":
			return "💬";
		default:
			return "📄";
	}
}

/**
 * Get visibility icon
 */
export function getVisibilityIcon(visibility: string): string {
	switch (visibility) {
		case "public":
			return "🌐";
		case "followers":
			return "👥";
		case "mentioned":
			return "📫";
		case "private":
			return "🔒";
		default:
			return "📄";
	}
}

/**
 * Get staleness indicator
 */
export function getStalenessIndicator(post: {
	isStale?: boolean;
	validUntil?: string;
}): string {
	if (post.isStale) return "{red-fg}⚠ STALE{/}";
	if (post.validUntil) {
		const validDate = new Date(post.validUntil);
		const now = new Date();
		const daysLeft = Math.ceil(
			(validDate.getTime() - now.getTime()) / 86400000
		);
		if (daysLeft <= 7) return `{yellow-fg}⏰ ${daysLeft}d{/}`;
	}
	return "";
}

/**
 * AgentNet Wall View
 * TUI component for viewing an agent's wall (posts)
 */

import type { BoxInterface, ListInterface } from "neo-neo-bblessed";
import { box, list } from "neo-neo-bblessed";
import type { AgentProfile, Post } from "../types/index.ts";
import {
	createScreen,
	formatRelativeTime,
	getAgentAvatar,
	getPostTypeIcon,
	getStalenessIndicator,
	truncate,
} from "./screen.ts";

/**
 * Format post for list display
 */
function formatPostItem(post: Post, maxWidth = 80): string {
	const icon = getPostTypeIcon(post.type);
	const time = formatRelativeTime(post.createdDate);
	const staleness = getStalenessIndicator(post);
	const title = post.title ? `{bold}${truncate(post.title, 40)}{/}` : "";
	const preview = truncate(
		post.content.replace(/\n/g, " ").trim(),
		maxWidth - 30
	);

	const meta: string[] = [];
	if (post.repostCount && post.repostCount > 0) {
		meta.push(`{green-fg}🔄${post.repostCount}{/}`);
	}
	if (post.replyCount && post.replyCount > 0) {
		meta.push(`{blue-fg}💬${post.replyCount}{/}`);
	}
	if (post.tags?.length) {
		meta.push(`{yellow-fg}#${post.tags[0]}{/}`);
	}

	const metaStr = meta.length > 0 ? ` ${meta.join(" ")}` : "";
	const titlePart = title ? `${title} - ` : "";

	return `${icon} {gray-fg}${time}{/} ${staleness}${titlePart}${preview}${metaStr}`;
}

export interface WallViewOptions {
	onViewPost?: (post: Post) => Promise<void>;
	onRepost?: (post: Post) => Promise<void>;
	onReply?: (post: Post) => Promise<void>;
	onBack?: () => Promise<void>;
}

/**
 * Render wall view TUI
 */
export async function renderWallView(
	profile: AgentProfile,
	posts: Post[],
	options?: WallViewOptions
): Promise<void> {
	if (!process.stdout.isTTY) {
		// Plain text fallback
		const avatar = getAgentAvatar(profile);
		console.log(`=== ${avatar} ${profile.name}'s Wall ===\n`);
		for (const post of posts) {
			const icon = getPostTypeIcon(post.type);
			console.log(`${icon} [${post.createdDate}]`);
			if (post.title) console.log(`   ${post.title}`);
			console.log(`   ${post.content.slice(0, 200)}`);
			console.log("");
		}
		return;
	}

	if (posts.length === 0) {
		console.log(`${profile.name} has no posts yet.`);
		return;
	}

	await new Promise<void>((resolve) => {
		const screen = createScreen({
			title: `AgentNet - ${profile.name}'s Wall`,
		});

		const avatar = getAgentAvatar(profile);
		const headerBox = box({
			parent: screen,
			top: 0,
			left: 0,
			width: "100%",
			height: 3,
			content: `\n ${avatar} {bold}${profile.name}{/} - ${profile.role}`,
			tags: true,
		});

		const listBox = box({
			parent: screen,
			top: 3,
			left: 0,
			width: "100%",
			height: "100%-5",
			border: { type: "line" },
			label: ` Wall (${posts.length} posts) `,
			style: {
				border: { fg: "cyan" },
			},
		});

		const postList = list({
			parent: listBox,
			top: 0,
			left: 1,
			width: "100%-4",
			height: "100%-2",
			keys: false,
			mouse: true,
			scrollable: true,
			tags: true,
			style: {
				selected: { bg: "blue", fg: "white" },
			},
		});

		postList.setItems(posts.map((p) => formatPostItem(p)));

		const footerBox = box({
			parent: screen,
			bottom: 0,
			left: 0,
			height: 2,
			width: "100%",
			tags: true,
			content:
				" {cyan-fg}[↑↓]{/} Navigate | {cyan-fg}[Enter]{/} View Post | {cyan-fg}[R]{/} Repost | {cyan-fg}[C]{/} Reply | {cyan-fg}[B]{/} Back | {cyan-fg}[q/Esc]{/} Quit",
		});

		let currentIndex = 0;

		const updateSelection = () => {
			postList.select(currentIndex);
			screen.render();
		};

		screen.key(["up", "k"], () => {
			if (currentIndex > 0) {
				currentIndex--;
				updateSelection();
			}
		});

		screen.key(["down", "j"], () => {
			if (currentIndex < posts.length - 1) {
				currentIndex++;
				updateSelection();
			}
		});

		screen.key(["enter"], async () => {
			const post = posts[currentIndex];
			if (post && options?.onViewPost) {
				await options.onViewPost(post);
				screen.render();
			}
		});

		screen.key(["r", "R"], async () => {
			const post = posts[currentIndex];
			if (post && options?.onRepost) {
				await options.onRepost(post);
				screen.render();
			}
		});

		screen.key(["c", "C"], async () => {
			const post = posts[currentIndex];
			if (post && options?.onReply) {
				await options.onReply(post);
				screen.render();
			}
		});

		screen.key(["b", "B"], async () => {
			if (options?.onBack) {
				screen.destroy();
				await options.onBack();
				resolve();
				return;
			}
		});

		screen.key(["q", "escape", "C-c"], () => {
			screen.destroy();
			resolve();
		});

		postList.focus();
		updateSelection();
		screen.render();
	});
}

/**
 * Render post detail popup
 */
export async function renderPostDetail(
	post: Post,
	author?: AgentProfile,
	parentScreen?: ReturnType<typeof createScreen>
): Promise<void> {
	const screen = parentScreen || createScreen({ title: "Post Detail" });

	await new Promise<void>((resolve) => {
		const popup = box({
			parent: screen,
			top: "center",
			left: "center",
			width: "80%",
			height: "80%",
			border: { type: "line" },
			label: ` ${getPostTypeIcon(post.type)} Post `,
			tags: true,
			scrollable: true,
			alwaysScroll: true,
			keys: true,
			vi: true,
			style: {
				border: { fg: "cyan" },
			},
		});

		const avatar = author ? getAgentAvatar(author) : "🤖";
		const authorName = author?.name || post.authorId;
		const staleness = getStalenessIndicator(post);

		let repostInfo = "";
		if (post.type === "repost" && post.originalAuthorId) {
			repostInfo = `\n {gray-fg}🔄 Reposted from {bold}${post.originalAuthorId}{/}{/}`;
			if (post.repostComment) {
				repostInfo += `\n {italic}"${post.repostComment}"{/}`;
			}
		}

		let replyInfo = "";
		if (post.type === "reply" && post.replyToAuthorId) {
			replyInfo = `\n {gray-fg}💬 Replying to {bold}${post.replyToAuthorId}{/}{/}`;
		}

		const tags = post.tags?.length
			? `\n {yellow-fg}Tags: ${post.tags.map((t) => `#${t}`).join(" ")}{/}`
			: "";

		const mentions = post.mentions?.length
			? `\n {cyan-fg}Mentions: ${post.mentions.map((m) => `@${m}`).join(" ")}{/}`
			: "";

		const source = post.sourceEvent
			? `\n {gray-fg}Source: ${post.sourceEvent}${post.sourceRef ? ` (${post.sourceRef})` : ""}{/}`
			: "";

		const validity = post.validUntil
			? `\n {gray-fg}Valid until: ${post.validUntil}{/}`
			: "";

		const content = `
 ${avatar} {bold}${authorName}{/} ${staleness}
 {gray-fg}${formatRelativeTime(post.createdDate)}{/}
${repostInfo}${replyInfo}

 ${post.title ? `{bold}${post.title}{/}\n\n ` : ""}${post.content}
${tags}${mentions}${source}${validity}

 {gray-fg}─────────────────────────────────{/}
 🔄 ${post.repostCount || 0} reposts  💬 ${post.replyCount || 0} replies
`;

		popup.setContent(content);

		const closePopup = () => {
			popup.destroy();
			if (!parentScreen) {
				screen.destroy();
			}
			resolve();
		};

		popup.key(["escape", "q", "enter"], closePopup);
		popup.focus();
		screen.render();
	});
}

/**
 * AgentNet Message View
 * TUI component for viewing and sending messages (DMs)
 */

import type { BoxInterface, ListInterface } from "neo-neo-bblessed";
import { box, list } from "neo-neo-bblessed";
import type { AgentProfile, Message, MessageThread } from "../types/index.ts";
import {
	createScreen,
	formatRelativeTime,
	getAgentAvatar,
	truncate,
} from "./screen.ts";

/**
 * Format thread for list display
 */
function formatThreadItem(
	thread: MessageThread,
	currentAgentId: string,
	profiles: Map<string, AgentProfile>
): string {
	const otherParticipants = thread.participants.filter(
		(p) => p !== currentAgentId
	);
	const otherNames = otherParticipants
		.map((p) => {
			const profile = profiles.get(p);
			return profile ? `${getAgentAvatar(profile)} ${profile.name}` : p;
		})
		.join(", ");

	const time = thread.lastMessageDate
		? formatRelativeTime(thread.lastMessageDate)
		: formatRelativeTime(thread.createdDate);

	const unread =
		thread.unreadCount && thread.unreadCount > 0
			? `{red-fg}(${thread.unreadCount} new){/}`
			: "";

	const title = thread.title ? `{gray-fg}${truncate(thread.title, 30)}{/}` : "";

	return `${otherNames} ${unread} {gray-fg}${time}{/} ${title}`;
}

/**
 * Format message for display
 */
function formatMessage(
	message: Message,
	profiles: Map<string, AgentProfile>,
	isOwn: boolean
): string {
	const profile = profiles.get(message.authorId);
	const avatar = profile ? getAgentAvatar(profile) : "🤖";
	const name = profile?.name || message.authorId;
	const time = formatRelativeTime(message.createdDate);
	const readStatus = message.readAt
		? "{green-fg}✓{/}"
		: "{gray-fg}○{/}";

	const align = isOwn ? "{right}" : "";
	const color = isOwn ? "{cyan-fg}" : "{white-fg}";

	return `${align}${avatar} {bold}${name}{/} {gray-fg}${time}{/} ${isOwn ? readStatus : ""}
${color}${message.content}{/}
`;
}

export interface ThreadListOptions {
	currentAgentId: string;
	onSelectThread?: (thread: MessageThread) => Promise<void>;
	onNewThread?: () => Promise<void>;
	onBack?: () => Promise<void>;
}

/**
 * Render thread list TUI
 */
export async function renderThreadList(
	threads: MessageThread[],
	profiles: Map<string, AgentProfile>,
	options: ThreadListOptions
): Promise<void> {
	if (!process.stdout.isTTY) {
		// Plain text fallback
		console.log("=== Message Threads ===\n");
		for (const thread of threads) {
			const others = thread.participants
				.filter((p) => p !== options.currentAgentId)
				.join(", ");
			console.log(`📬 ${others}`);
			console.log(`   ${thread.messageCount} messages`);
			if (thread.lastMessageDate) {
				console.log(`   Last: ${thread.lastMessageDate}`);
			}
			console.log("");
		}
		return;
	}

	if (threads.length === 0) {
		console.log("No message threads yet.");
		return;
	}

	await new Promise<void>((resolve) => {
		const screen = createScreen({ title: "AgentNet - Messages" });

		const headerBox = box({
			parent: screen,
			top: 0,
			left: 0,
			width: "100%",
			height: 3,
			content: "\n {bold}AgentNet{/} - Message Threads",
			tags: true,
		});

		const listBox = box({
			parent: screen,
			top: 3,
			left: 0,
			width: "100%",
			height: "100%-5",
			border: { type: "line" },
			label: ` Threads (${threads.length}) `,
			style: {
				border: { fg: "cyan" },
			},
		});

		const threadList = list({
			parent: listBox,
			top: 0,
			left: 1,
			width: "100%-4",
			height: "100%-2",
			keys: false,
			mouse: true,
			scrollable: true,
			tags: true,
			style: {
				selected: { bg: "blue", fg: "white" },
			},
		});

		threadList.setItems(
			threads.map((t) =>
				formatThreadItem(t, options.currentAgentId, profiles)
			)
		);

		const footerBox = box({
			parent: screen,
			bottom: 0,
			left: 0,
			height: 2,
			width: "100%",
			tags: true,
			content:
				" {cyan-fg}[↑↓]{/} Navigate | {cyan-fg}[Enter]{/} Open Thread | {cyan-fg}[N]{/} New Thread | {cyan-fg}[B]{/} Back | {cyan-fg}[q/Esc]{/} Quit",
		});

		let currentIndex = 0;

		const updateSelection = () => {
			threadList.select(currentIndex);
			screen.render();
		};

		screen.key(["up", "k"], () => {
			if (currentIndex > 0) {
				currentIndex--;
				updateSelection();
			}
		});

		screen.key(["down", "j"], () => {
			if (currentIndex < threads.length - 1) {
				currentIndex++;
				updateSelection();
			}
		});

		screen.key(["enter"], async () => {
			const thread = threads[currentIndex];
			if (thread && options.onSelectThread) {
				screen.destroy();
				await options.onSelectThread(thread);
				resolve();
			}
		});

		screen.key(["n", "N"], async () => {
			if (options.onNewThread) {
				await options.onNewThread();
				screen.render();
			}
		});

		screen.key(["b", "B"], async () => {
			if (options.onBack) {
				screen.destroy();
				await options.onBack();
				resolve();
				return;
			}
		});

		screen.key(["q", "escape", "C-c"], () => {
			screen.destroy();
			resolve();
		});

		threadList.focus();
		updateSelection();
		screen.render();
	});
}

export interface ThreadViewOptions {
	currentAgentId: string;
	onSendMessage?: (content: string) => Promise<void>;
	onBack?: () => Promise<void>;
}

/**
 * Render thread view TUI
 */
export async function renderThreadView(
	thread: MessageThread,
	messages: Message[],
	profiles: Map<string, AgentProfile>,
	options: ThreadViewOptions
): Promise<void> {
	if (!process.stdout.isTTY) {
		// Plain text fallback
		const others = thread.participants
			.filter((p) => p !== options.currentAgentId)
			.join(", ");
		console.log(`=== Thread with ${others} ===\n`);
		for (const msg of messages) {
			const profile = profiles.get(msg.authorId);
			const name = profile?.name || msg.authorId;
			console.log(`${name} [${msg.createdDate}]:`);
			console.log(`  ${msg.content}`);
			console.log("");
		}
		return;
	}

	await new Promise<void>((resolve) => {
		const screen = createScreen({ title: "AgentNet - Thread" });

		const otherParticipants = thread.participants.filter(
			(p) => p !== options.currentAgentId
		);
		const otherNames = otherParticipants
			.map((p) => {
				const profile = profiles.get(p);
				return profile
					? `${getAgentAvatar(profile)} ${profile.name}`
					: p;
			})
			.join(", ");

		const headerBox = box({
			parent: screen,
			top: 0,
			left: 0,
			width: "100%",
			height: 3,
			content: `\n {bold}Thread with{/} ${otherNames}`,
			tags: true,
		});

		const messageBox = box({
			parent: screen,
			top: 3,
			left: 0,
			width: "100%",
			height: "100%-7",
			border: { type: "line" },
			label: ` Messages (${messages.length}) `,
			tags: true,
			scrollable: true,
			alwaysScroll: true,
			keys: true,
			vi: true,
			style: {
				border: { fg: "cyan" },
			},
		});

		const messageContent = messages
			.map((m) =>
				formatMessage(m, profiles, m.authorId === options.currentAgentId)
			)
			.join("\n");

		messageBox.setContent(messageContent);
		messageBox.setScrollPerc(100); // Scroll to bottom

		const footerBox = box({
			parent: screen,
			bottom: 0,
			left: 0,
			height: 4,
			width: "100%",
			tags: true,
			content:
				"\n {cyan-fg}[↑↓/j/k]{/} Scroll | {cyan-fg}[B]{/} Back | {cyan-fg}[q/Esc]{/} Quit\n {gray-fg}(Message composition coming soon){/}",
		});

		screen.key(["up", "k"], () => {
			messageBox.scroll(-1);
			screen.render();
		});

		screen.key(["down", "j"], () => {
			messageBox.scroll(1);
			screen.render();
		});

		screen.key(["pageup"], () => {
			messageBox.scroll(-10);
			screen.render();
		});

		screen.key(["pagedown"], () => {
			messageBox.scroll(10);
			screen.render();
		});

		screen.key(["b", "B"], async () => {
			if (options.onBack) {
				screen.destroy();
				await options.onBack();
				resolve();
				return;
			}
		});

		screen.key(["q", "escape", "C-c"], () => {
			screen.destroy();
			resolve();
		});

		messageBox.focus();
		screen.render();
	});
}

