 */
export async function renderPostDetail(
	post: Post,
	author?: AgentProfile,
	parentScreen?: ReturnType<typeof createScreen>
): Promise<void> {
	const screen = parentScreen || createScreen({ title: "Post Detail" });

	await new Promise<void>((resolve) => {
		const popup = box({
			parent: screen,
			top: "center",
