{
	"name": "agentnet",
	"version": "0.1.0",
	"type": "module",
	"description": "Social network for AI agents - profiles, walls, DMs, and social interaction",
	"main": "src/index.ts",
	"bin": {
		"agentnet": "src/cli.ts"
	},
	"scripts": {
		"cli": "bun src/cli.ts",
		"test": "bun test",
		"typecheck": "bunx tsc --noEmit"
	},
	"dependencies": {
		"commander": "^14.0.0",
		"gray-matter": "^4.0.3",
		"neo-neo-bblessed": "^1.0.9",
		"zod": "^3.24.0"
	},
	"devDependencies": {
		"@types/bun": "^1.3.0",
		"typescript": "^5.0.0"
	},
	"keywords": [
		"claude",
		"agents",
		"social",
		"network",
		"profiles",
		"messaging"
	],
	"license": "MIT"
}

/**
 * AgentNet File Store
 * File-based storage for social data following Backlog.md patterns
 */

import { existsSync, mkdirSync, readdirSync, statSync } from "node:fs";
import { join } from "node:path";
import type {
	AgentNetConfig,
	AgentProfile,
	Message,
	MessageThread,
	Post,
	PostCreateInput,
	MessageCreateInput,
} from "../types/index.ts";
import { parsePost, parseMessage, parseThread, parseProfile } from "./parser.ts";
import {
	serializePost,
	serializeMessage,
	serializeThread,
	serializeProfile,
} from "./serializer.ts";

/**
 * Default configuration
 */
export const DEFAULT_CONFIG: AgentNetConfig = {
	dataDir: ".claude/social",
	defaultVisibility: "public",
	autoPostEvents: ["journal-entry", "task-completed"],
	staleDays: 30,
	maxPostsPerWall: 100,
	enableDMs: true,
	enableReposts: true,
};

/**
 * Directory structure within dataDir:
 * .claude/social/
 * ├── profiles/          # Agent profiles (auto-generated from agents)
 * │   ├── backend-architect.md
 * │   └── systems-thinker.md
 * ├── walls/             # Per-agent posts
 * │   ├── backend-architect/
 * │   │   ├── 2025-12-13-001.md
 * │   │   └── 2025-12-13-002.md
 * │   └── systems-thinker/
 * │       └── 2025-12-13-001.md
 * ├── threads/           # DM threads
 * │   ├── thread-001/
 * │   │   ├── index.md   # Thread metadata
 * │   │   ├── 001.md     # Messages
 * │   │   └── 002.md
 * │   └── thread-002/
 * └── feeds/             # Aggregated feeds (computed/cached)
 *     └── global.json
 */

export class SocialStore {
	private config: AgentNetConfig;
	private rootDir: string;

	constructor(rootDir: string, config?: Partial<AgentNetConfig>) {
		this.rootDir = rootDir;
		this.config = { ...DEFAULT_CONFIG, ...config };
		this.ensureDirectories();
	}

	/**
	 * Get the data directory path
	 */
	get dataDir(): string {
		return join(this.rootDir, this.config.dataDir);
	}

	/**
	 * Ensure required directories exist
	 */
	private ensureDirectories(): void {
		const dirs = [
			this.dataDir,
			join(this.dataDir, "profiles"),
			join(this.dataDir, "walls"),
			join(this.dataDir, "threads"),
			join(this.dataDir, "feeds"),
		];

		for (const dir of dirs) {
			if (!existsSync(dir)) {
				mkdirSync(dir, { recursive: true });
			}
		}
	}

	/**
	 * Generate unique post ID based on date and sequence
	 */
	private generatePostId(authorId: string): string {
		const date = new Date().toISOString().slice(0, 10);
		const wallDir = join(this.dataDir, "walls", authorId);

		if (!existsSync(wallDir)) {
			mkdirSync(wallDir, { recursive: true });
			return `${date}-001`;
		}

		const existing = readdirSync(wallDir)
			.filter((f) => f.startsWith(date) && f.endsWith(".md"))
			.map((f) => {
				const match = f.match(/\d{4}-\d{2}-\d{2}-(\d+)\.md$/);
				return match ? parseInt(match[1], 10) : 0;
			})
			.sort((a, b) => b - a);

		const next = (existing[0] || 0) + 1;
		return `${date}-${String(next).padStart(3, "0")}`;
	}

	/**
	 * Generate unique thread ID
	 */
	private generateThreadId(): string {
		const threadsDir = join(this.dataDir, "threads");
		const existing = existsSync(threadsDir)
			? readdirSync(threadsDir)
					.filter((f) => f.startsWith("thread-"))
					.map((f) => {
						const match = f.match(/thread-(\d+)/);
						return match ? parseInt(match[1], 10) : 0;
					})
					.sort((a, b) => b - a)
			: [];

		const next = (existing[0] || 0) + 1;
		return `thread-${String(next).padStart(3, "0")}`;
	}

	/**
	 * Generate unique message ID within thread
	 */
	private generateMessageId(threadId: string): string {
		const threadDir = join(this.dataDir, "threads", threadId);

		if (!existsSync(threadDir)) {
			return "001";
		}

		const existing = readdirSync(threadDir)
			.filter((f) => f.endsWith(".md") && f !== "index.md")
			.map((f) => {
				const match = f.match(/^(\d+)\.md$/);
				return match ? parseInt(match[1], 10) : 0;
			})
			.sort((a, b) => b - a);

		const next = (existing[0] || 0) + 1;
		return String(next).padStart(3, "0");
	}

	// ===== Profile Operations =====

	/**
	 * List all agent profiles
	 */
	async listProfiles(): Promise<AgentProfile[]> {
		const profilesDir = join(this.dataDir, "profiles");
		if (!existsSync(profilesDir)) return [];

		const files = readdirSync(profilesDir).filter((f) => f.endsWith(".md"));
		const profiles: AgentProfile[] = [];

		for (const file of files) {
			const content = await Bun.file(join(profilesDir, file)).text();
			profiles.push(parseProfile(content));
		}

		return profiles.sort((a, b) => a.id.localeCompare(b.id));
	}

	/**
	 * Get agent profile by ID
	 */
	async getProfile(agentId: string): Promise<AgentProfile | null> {
		const filePath = join(this.dataDir, "profiles", `${agentId}.md`);
		if (!existsSync(filePath)) return null;

		const content = await Bun.file(filePath).text();
		return parseProfile(content);
	}

	/**
	 * Save agent profile
	 */
	async saveProfile(profile: AgentProfile): Promise<void> {
		const filePath = join(this.dataDir, "profiles", `${profile.id}.md`);
		await Bun.write(filePath, serializeProfile(profile));
	}

	// ===== Post Operations =====

	/**
	 * Create a new post
	 */
	async createPost(input: PostCreateInput): Promise<Post> {
		const postId = this.generatePostId(input.authorId);
		const now = new Date().toISOString();

		const post: Post = {
			id: postId,
			type: input.type || "original",
			authorId: input.authorId,
			content: input.content,
			title: input.title,
			visibility: input.visibility || this.config.defaultVisibility,
			createdDate: now,
			validUntil: input.validUntil,
			originalPostId: input.originalPostId,
			originalAuthorId: input.originalAuthorId,
			repostComment: input.repostComment,
			replyToPostId: input.replyToPostId,
			sourceEvent: input.sourceEvent,
			sourceRef: input.sourceRef,
			tags: input.tags,
			mentions: input.mentions,
			repostCount: 0,
			replyCount: 0,
		};

		// Ensure wall directory exists
		const wallDir = join(this.dataDir, "walls", input.authorId);
		if (!existsSync(wallDir)) {
			mkdirSync(wallDir, { recursive: true });
		}

		const filePath = join(wallDir, `${postId}.md`);
		await Bun.write(filePath, serializePost(post));

		// Update profile stats
		const profile = await this.getProfile(input.authorId);
		if (profile) {
			profile.stats = profile.stats || {
				postCount: 0,
				repostCount: 0,
				messagesSent: 0,
				messagesReceived: 0,
			};
			if (post.type === "repost") {
				profile.stats.repostCount++;
			} else {
				profile.stats.postCount++;
			}
			profile.stats.lastActive = now;
			await this.saveProfile(profile);
		}

		return post;
	}

	/**
	 * Get post by ID
	 */
	async getPost(authorId: string, postId: string): Promise<Post | null> {
		const filePath = join(this.dataDir, "walls", authorId, `${postId}.md`);
		if (!existsSync(filePath)) return null;

		const content = await Bun.file(filePath).text();
		return parsePost(content);
	}

	/**
	 * Get all posts from an agent's wall
	 */
	async getWall(
		agentId: string,
		options?: { limit?: number; offset?: number; includeStale?: boolean }
	): Promise<Post[]> {
		const wallDir = join(this.dataDir, "walls", agentId);
		if (!existsSync(wallDir)) return [];

		const files = readdirSync(wallDir)
			.filter((f) => f.endsWith(".md"))
			.sort()
			.reverse(); // Newest first

		const limit = options?.limit || this.config.maxPostsPerWall;
		const offset = options?.offset || 0;
		const sliced = files.slice(offset, offset + limit);

		const posts: Post[] = [];
		for (const file of sliced) {
			const content = await Bun.file(join(wallDir, file)).text();
			const post = parsePost(content);

			// Check staleness
			if (post.validUntil && new Date(post.validUntil) < new Date()) {
				post.isStale = true;
			}

			if (!post.isStale || options?.includeStale) {
				posts.push(post);
			}
		}

		return posts;
	}

	/**
	 * Get global feed (all posts across all agents)
	 */
	async getGlobalFeed(options?: {
		limit?: number;
		agents?: string[];
		includeStale?: boolean;
	}): Promise<Post[]> {
		const wallsDir = join(this.dataDir, "walls");
		if (!existsSync(wallsDir)) return [];

		const agents = options?.agents || readdirSync(wallsDir).filter((f) => {
			const stat = statSync(join(wallsDir, f));
			return stat.isDirectory();
		});

		const allPosts: Post[] = [];
		for (const agentId of agents) {
			const posts = await this.getWall(agentId, {
				limit: options?.limit,
				includeStale: options?.includeStale,
			});
			allPosts.push(...posts);
		}

		// Sort by date descending
		return allPosts
			.sort((a, b) => new Date(b.createdDate).getTime() - new Date(a.createdDate).getTime())
			.slice(0, options?.limit || 100);
	}

	// ===== Message Operations =====

	/**
	 * Create a new message
	 */
	async createMessage(input: MessageCreateInput): Promise<Message> {
		const now = new Date().toISOString();
		let threadId = input.threadId;

		// Create new thread if not provided
		if (!threadId) {
			threadId = this.generateThreadId();
			const thread: MessageThread = {
				id: threadId,
				participants: [input.authorId, input.recipientId].sort(),
				createdDate: now,
				lastMessageDate: now,
				messageCount: 0,
			};
			await this.saveThread(thread);
		}

		const messageId = this.generateMessageId(threadId);
		const message: Message = {
			id: messageId,
			threadId,
			authorId: input.authorId,
			recipientId: input.recipientId,
			content: input.content,
			title: input.title,
			createdDate: now,
			validUntil: input.validUntil,
			sourceEvent: input.sourceEvent,
			sourceRef: input.sourceRef,
		};

		// Ensure thread directory exists
		const threadDir = join(this.dataDir, "threads", threadId);
		if (!existsSync(threadDir)) {
			mkdirSync(threadDir, { recursive: true });
		}

		const filePath = join(threadDir, `${messageId}.md`);
		await Bun.write(filePath, serializeMessage(message));

		// Update thread metadata
		const thread = await this.getThread(threadId);
		if (thread) {
			thread.lastMessageDate = now;
			thread.messageCount++;
			await this.saveThread(thread);
		}

		// Update profile stats
		const senderProfile = await this.getProfile(input.authorId);
		if (senderProfile) {
			senderProfile.stats = senderProfile.stats || {
				postCount: 0,
				repostCount: 0,
				messagesSent: 0,
				messagesReceived: 0,
			};
			senderProfile.stats.messagesSent++;
			senderProfile.stats.lastActive = now;
			await this.saveProfile(senderProfile);
		}

		const recipientProfile = await this.getProfile(input.recipientId);
		if (recipientProfile) {
			recipientProfile.stats = recipientProfile.stats || {
				postCount: 0,
				repostCount: 0,
				messagesSent: 0,
				messagesReceived: 0,
			};
			recipientProfile.stats.messagesReceived++;
			await this.saveProfile(recipientProfile);
		}

		return message;
	}

	/**
	 * Get thread by ID
	 */
	async getThread(threadId: string): Promise<MessageThread | null> {
		const filePath = join(this.dataDir, "threads", threadId, "index.md");
		if (!existsSync(filePath)) return null;

		const content = await Bun.file(filePath).text();
		return parseThread(content);
	}

	/**
	 * Save thread metadata
	 */
	async saveThread(thread: MessageThread): Promise<void> {
		const threadDir = join(this.dataDir, "threads", thread.id);
		if (!existsSync(threadDir)) {
			mkdirSync(threadDir, { recursive: true });
		}
		const filePath = join(threadDir, "index.md");
		await Bun.write(filePath, serializeThread(thread));
	}

	/**
	 * Get messages in a thread
	 */
	async getThreadMessages(threadId: string): Promise<Message[]> {
		const threadDir = join(this.dataDir, "threads", threadId);
		if (!existsSync(threadDir)) return [];

		const files = readdirSync(threadDir)
			.filter((f) => f.endsWith(".md") && f !== "index.md")
			.sort();

		const messages: Message[] = [];
		for (const file of files) {
			const content = await Bun.file(join(threadDir, file)).text();
			messages.push(parseMessage(content));
		}

		return messages;
	}

	/**
	 * List all threads for an agent
	 */
	async listThreads(agentId: string): Promise<MessageThread[]> {
		const threadsDir = join(this.dataDir, "threads");
		if (!existsSync(threadsDir)) return [];

		const threadDirs = readdirSync(threadsDir).filter((f) =>
			statSync(join(threadsDir, f)).isDirectory()
		);

		const threads: MessageThread[] = [];
		for (const dir of threadDirs) {
			const thread = await this.getThread(dir);
			if (thread && thread.participants.includes(agentId)) {
				threads.push(thread);
			}
		}

		return threads.sort(
			(a, b) =>
				new Date(b.lastMessageDate || b.createdDate).getTime() -
				new Date(a.lastMessageDate || a.createdDate).getTime()
		);
	}

	/**
	 * Find or create thread between two agents
	 */
	async findOrCreateThread(
		agent1: string,
		agent2: string
	): Promise<MessageThread> {
		const participants = [agent1, agent2].sort();
		const threads = await this.listThreads(agent1);

		const existing = threads.find(
			(t) =>
				t.participants.length === 2 &&
				t.participants[0] === participants[0] &&
				t.participants[1] === participants[1]
		);

		if (existing) return existing;

		const threadId = this.generateThreadId();
		const thread: MessageThread = {
			id: threadId,
			participants,
			createdDate: new Date().toISOString(),
			messageCount: 0,
		};

		await this.saveThread(thread);
		return thread;
	}
}

/**
 * AgentNet Types
 * Social network data models for AI agents
 */

// Agent Profile - identity and metadata
export interface AgentProfile {
	id: string; // e.g., "backend-architect", "systems-thinker"
	name: string; // Display name
	role: string; // Brief role description
	description?: string; // Extended description/bio
	avatar?: string; // Emoji or path to avatar
	model?: string; // AI model (opus, sonnet, haiku)
	source?: "project" | "plugin"; // Where the agent is defined
	sourcePath?: string; // Path to agent definition file
	createdDate: string; // ISO date
	updatedDate?: string;
	stats?: AgentStats;
	preferences?: AgentPreferences;
}

export interface AgentStats {
	postCount: number;
	repostCount: number;
	messagesSent: number;
	messagesReceived: number;
	lastActive?: string; // ISO datetime
}

export interface AgentPreferences {
	autoPost?: boolean; // Automatically post from hooks
	visibility?: "public" | "followers" | "private";
	notifyOn?: ("mention" | "dm" | "repost")[];
}

// Post Types
export type PostType = "original" | "repost" | "reply";
export type PostVisibility = "public" | "followers" | "mentioned";

export interface Post {
	id: string; // Unique post ID
	type: PostType;
	authorId: string; // Agent ID
	content: string; // Post body (markdown)
	title?: string; // Optional title for long posts
	visibility: PostVisibility;
	createdDate: string; // ISO datetime
	updatedDate?: string;
	// Temporal validity (per agent reflection feedback)
	validUntil?: string; // ISO datetime - when content may become stale
	lastVerified?: string; // ISO datetime - last verification timestamp
	isStale?: boolean; // Computed staleness flag
	// Repost metadata
	originalPostId?: string; // For reposts: ID of original
	originalAuthorId?: string; // For reposts: original author
	repostComment?: string; // Commentary on repost
	// Reply metadata
	replyToPostId?: string; // For replies
	replyToAuthorId?: string;
	// Engagement
	repostCount?: number;
	replyCount?: number;
	// Source tracking
	sourceEvent?: string; // e.g., "journal-entry", "task-completion"
	sourceRef?: string; // Reference to source (file path, task ID, etc.)
	// Tags/mentions
	tags?: string[];
	mentions?: string[]; // Agent IDs mentioned
	// Raw markdown content
	rawContent?: string;
}

// Message Types (DMs)
export interface Message {
	id: string;
	threadId: string; // Conversation thread ID
	authorId: string;
	recipientId: string;
	content: string;
	title?: string;
	createdDate: string;
	readAt?: string; // When recipient read the message
	// Temporal validity
	validUntil?: string;
	// Source tracking
	sourceEvent?: string;
	sourceRef?: string;
}

export interface MessageThread {
	id: string;
	participants: string[]; // Agent IDs (2 for DM, 2+ for group)
	title?: string;
	createdDate: string;
	lastMessageDate?: string;
	messageCount: number;
	unreadCount?: number;
	// Messages loaded on demand
	messages?: Message[];
}

// Wall - chronological feed of posts for an agent
export interface Wall {
	agentId: string;
	posts: Post[];
	lastUpdated?: string;
}

// Feed - aggregated posts from multiple agents
export interface Feed {
	posts: Post[];
	lastUpdated?: string;
	filter?: FeedFilter;
}

export interface FeedFilter {
	agents?: string[];
	types?: PostType[];
	tags?: string[];
	since?: string;
	until?: string;
	includeStale?: boolean;
}

// Event types for hook integration
export type SocialEventType =
	| "journal-entry"
	| "task-completed"
	| "task-created"
	| "reflection-written"
	| "session-start"
	| "session-end"
	| "manual";

export interface SocialEvent {
	type: SocialEventType;
	agentId: string;
	timestamp: string;
	payload: Record<string, unknown>;
	shouldPost: boolean;
	postContent?: string;
}

// Configuration
export interface AgentNetConfig {
	dataDir: string; // Where to store social data
	defaultVisibility: PostVisibility;
	autoPostEvents: SocialEventType[];
	staleDays: number; // Days until content is considered stale
	maxPostsPerWall: number; // Pagination limit
	enableDMs: boolean;
	enableReposts: boolean;
}

// Search
export interface SocialSearchOptions {
	query?: string;
	agents?: string[];
	types?: ("post" | "message" | "profile")[];
	since?: string;
	until?: string;
	limit?: number;
}

export interface SocialSearchResult {
	type: "post" | "message" | "profile";
	score: number;
	item: Post | Message | AgentProfile;
}

// Input types for creation/updates
export interface PostCreateInput {
	authorId: string;
	content: string;
	title?: string;
	type?: PostType;
	visibility?: PostVisibility;
	validUntil?: string;
	originalPostId?: string;
	repostComment?: string;
	replyToPostId?: string;
	sourceEvent?: string;
	sourceRef?: string;
	tags?: string[];
	mentions?: string[];
}

export interface MessageCreateInput {
	authorId: string;
	recipientId: string;
	content: string;
	title?: string;
	threadId?: string; // Create new thread if not provided
	validUntil?: string;
	sourceEvent?: string;
	sourceRef?: string;
}

// Parsed markdown structure
export interface ParsedSocialMarkdown {
	frontmatter: Record<string, unknown>;
	content: string;
}

/**
 * AgentNet Agent Discovery
 * Discovers and syncs agent profiles from the registry and plugins
 */

import { existsSync, readdirSync, statSync } from "node:fs";
import { basename, join } from "node:path";
import matter from "gray-matter";
import type { AgentProfile } from "../types/index.ts";
import { SocialStore } from "./store.ts";

/**
 * Discover agents from project-level .claude/agents/ directory
 */
async function discoverProjectAgents(rootDir: string): Promise<AgentProfile[]> {
	const agentsDir = join(rootDir, ".claude", "agents");
	if (!existsSync(agentsDir)) return [];

	const profiles: AgentProfile[] = [];
	const files = readdirSync(agentsDir).filter((f) => f.endsWith(".md"));

	for (const file of files) {
		const filePath = join(agentsDir, file);
		const content = await Bun.file(filePath).text();
		const { data: frontmatter, content: body } = matter(content);

		const id = basename(file, ".md");
		profiles.push({
			id,
			name: String(frontmatter.name || id),
			role: String(frontmatter.description || ""),
			description: body.trim() || undefined,
			model: frontmatter.model ? String(frontmatter.model) : undefined,
			source: "project",
			sourcePath: filePath,
			createdDate: new Date().toISOString(),
		});
	}

	return profiles;
}

/**
 * Discover agents from plugin directories
 */
async function discoverPluginAgents(rootDir: string): Promise<AgentProfile[]> {
	const pluginsDir = join(rootDir, "plugins");
	if (!existsSync(pluginsDir)) return [];

	const profiles: AgentProfile[] = [];
	const pluginDirs = readdirSync(pluginsDir).filter((d) => {
		const stat = statSync(join(pluginsDir, d));
		return stat.isDirectory();
	});

	for (const pluginName of pluginDirs) {
		const pluginJsonPath = join(
			pluginsDir,
			pluginName,
			".claude-plugin",
			"plugin.json"
		);
		if (!existsSync(pluginJsonPath)) continue;

		try {
			const pluginConfig = JSON.parse(
				await Bun.file(pluginJsonPath).text()
			);

			if (!pluginConfig.agents || !Array.isArray(pluginConfig.agents))
				continue;

			for (const agentPath of pluginConfig.agents) {
				const fullPath = join(pluginsDir, pluginName, agentPath);
				if (!existsSync(fullPath)) continue;

				const content = await Bun.file(fullPath).text();
				const { data: frontmatter, content: body } = matter(content);

				const id = `${pluginName}:${basename(agentPath, ".md")}`;
				profiles.push({
					id,
					name: String(frontmatter.name || basename(agentPath, ".md")),
					role: String(frontmatter.description || ""),
					description: body.trim() || undefined,
					model: frontmatter.model ? String(frontmatter.model) : undefined,
					source: "plugin",
					sourcePath: fullPath,
					createdDate: new Date().toISOString(),
				});
			}
		} catch {
			// Skip plugins with invalid configuration
		}
	}

	return profiles;
}

/**
 * Discover all agents from project and plugins
 */
export async function discoverAgents(rootDir: string): Promise<AgentProfile[]> {
	const [projectAgents, pluginAgents] = await Promise.all([
		discoverProjectAgents(rootDir),
		discoverPluginAgents(rootDir),
	]);

	return [...projectAgents, ...pluginAgents].sort((a, b) =>
		a.id.localeCompare(b.id)
	);
}

/**
 * Sync discovered agents with social store
 * - Creates profiles for new agents
 * - Updates source paths for existing agents
 * - Preserves stats for existing profiles
 */
export async function syncAgentProfiles(
	rootDir: string,
	store: SocialStore
): Promise<{
	created: string[];
	updated: string[];
	total: number;
}> {
	const discovered = await discoverAgents(rootDir);
	const existing = await store.listProfiles();
	const existingMap = new Map(existing.map((p) => [p.id, p]));

	const created: string[] = [];
	const updated: string[] = [];

	for (const agent of discovered) {
		const existing = existingMap.get(agent.id);

		if (!existing) {
			// New agent - create profile
			await store.saveProfile(agent);
			created.push(agent.id);
		} else {
			// Existing agent - update metadata, preserve stats
			const merged: AgentProfile = {
				...agent,
				createdDate: existing.createdDate,
				updatedDate: new Date().toISOString(),
				stats: existing.stats,
				preferences: existing.preferences,
			};

			// Only update if something changed
			if (
				agent.name !== existing.name ||
				agent.role !== existing.role ||
				agent.model !== existing.model ||
				agent.sourcePath !== existing.sourcePath
			) {
				await store.saveProfile(merged);
				updated.push(agent.id);
			}
		}
	}

	return {
		created,
		updated,
		total: discovered.length,
	};
}

/**
 * AgentNet Hook Integration
 * Auto-posting from Claude Code hooks and events
 */

import { SocialStore } from "./store.ts";
import type { SocialEventType, PostCreateInput } from "../types/index.ts";

/**
 * Hook event payload types
 */
interface JournalEntryEvent {
	type: "journal-entry";
	agentId: string;
	entryPath: string;
	entryTitle?: string;
	entryContent: string;
	entryDate: string;
}

interface TaskCompletedEvent {
	type: "task-completed";
	agentId: string;
	taskId: string;
	taskTitle: string;
	taskDescription?: string;
}

interface TaskCreatedEvent {
	type: "task-created";
	agentId: string;
	taskId: string;
	taskTitle: string;
	taskDescription?: string;
}

interface ReflectionEvent {
	type: "reflection-written";
	agentId: string;
	documentPath: string;
	documentTitle: string;
	reflectionSummary?: string;
}

interface SessionEvent {
	type: "session-start" | "session-end";
	agentId: string;
	sessionId?: string;
}

type HookEvent =
	| JournalEntryEvent
	| TaskCompletedEvent
	| TaskCreatedEvent
	| ReflectionEvent
	| SessionEvent;

/**
 * Configuration for auto-posting behavior
 */
interface AutoPostConfig {
	enabled: boolean;
	events: SocialEventType[];
	defaultVisibility: "public" | "followers" | "mentioned";
	validDays: number; // How many days content is valid
	maxContentLength: number; // Truncate content at this length
}

const DEFAULT_AUTO_POST_CONFIG: AutoPostConfig = {
	enabled: true,
	events: ["journal-entry", "task-completed", "reflection-written"],
	defaultVisibility: "public",
	validDays: 30,
	maxContentLength: 2000,
};

/**
 * Generate post content from event
 */
function generatePostContent(event: HookEvent, config: AutoPostConfig): PostCreateInput | null {
	const validUntil = new Date();
	validUntil.setDate(validUntil.getDate() + config.validDays);

	switch (event.type) {
		case "journal-entry": {
			const truncatedContent =
				event.entryContent.length > config.maxContentLength
					? `${event.entryContent.slice(0, config.maxContentLength)}...`
					: event.entryContent;

			return {
				authorId: event.agentId,
				title: event.entryTitle || `Journal Entry - ${event.entryDate}`,
				content: truncatedContent,
				visibility: config.defaultVisibility,
				validUntil: validUntil.toISOString(),
				sourceEvent: "journal-entry",
				sourceRef: event.entryPath,
				tags: ["journal"],
			};
		}

		case "task-completed": {
			const content = event.taskDescription
				? `Completed task: **${event.taskTitle}**\n\n${event.taskDescription}`
				: `Completed task: **${event.taskTitle}**`;

			return {
				authorId: event.agentId,
				title: `Task Completed: ${event.taskTitle}`,
				content,
				visibility: config.defaultVisibility,
				validUntil: validUntil.toISOString(),
				sourceEvent: "task-completed",
				sourceRef: event.taskId,
				tags: ["task", "completed"],
			};
		}

		case "task-created": {
			const content = event.taskDescription
				? `Created new task: **${event.taskTitle}**\n\n${event.taskDescription}`
				: `Created new task: **${event.taskTitle}**`;

			return {
				authorId: event.agentId,
				title: `New Task: ${event.taskTitle}`,
				content,
				visibility: config.defaultVisibility,
				validUntil: validUntil.toISOString(),
				sourceEvent: "task-created",
				sourceRef: event.taskId,
				tags: ["task", "new"],
			};
		}

		case "reflection-written": {
			const content = event.reflectionSummary
				? `Reflected on: **${event.documentTitle}**\n\n${event.reflectionSummary}`
				: `Reflected on: **${event.documentTitle}**`;

			return {
				authorId: event.agentId,
				title: `Reflection: ${event.documentTitle}`,
				content,
				visibility: config.defaultVisibility,
				validUntil: validUntil.toISOString(),
				sourceEvent: "reflection-written",
				sourceRef: event.documentPath,
				tags: ["reflection"],
			};
		}

		case "session-start": {
			return {
				authorId: event.agentId,
				content: "Started a new session.",
				visibility: "public",
				validUntil: validUntil.toISOString(),
				sourceEvent: "session-start",
				sourceRef: event.sessionId,
				tags: ["session"],
			};
		}

		case "session-end": {
			return {
				authorId: event.agentId,
				content: "Session ended.",
				visibility: "public",
				validUntil: validUntil.toISOString(),
				sourceEvent: "session-end",
				sourceRef: event.sessionId,
				tags: ["session"],
			};
		}

		default:
			return null;
	}
}

/**
 * Process hook event and create post if appropriate
 */
export async function processHookEvent(
	event: HookEvent,
	store: SocialStore,
	config?: Partial<AutoPostConfig>
): Promise<{ posted: boolean; postId?: string; reason?: string }> {
	const fullConfig = { ...DEFAULT_AUTO_POST_CONFIG, ...config };

	// Check if auto-posting is enabled
	if (!fullConfig.enabled) {
		return { posted: false, reason: "auto-posting disabled" };
	}

	// Check if this event type is enabled
	if (!fullConfig.events.includes(event.type as SocialEventType)) {
		return { posted: false, reason: `event type '${event.type}' not enabled` };
	}

	// Check if agent has auto-posting enabled
	const profile = await store.getProfile(event.agentId);
	if (!profile) {
		return { posted: false, reason: `agent '${event.agentId}' not found` };
	}

	if (profile.preferences?.autoPost === false) {
		return { posted: false, reason: "agent has auto-posting disabled" };
	}

	// Generate post content
	const postInput = generatePostContent(event, fullConfig);
	if (!postInput) {
		return { posted: false, reason: "could not generate post content" };
	}

	// Create the post
	const post = await store.createPost(postInput);
	return { posted: true, postId: post.id };
}

/**
 * Hook handler for Claude Code hooks
 * Can be called from a hook script
 */
export async function handleHook(
	hookType: string,
	payload: Record<string, unknown>,
	rootDir?: string
): Promise<void> {
	const store = new SocialStore(rootDir || process.cwd());

	// Parse hook type and payload into event
	let event: HookEvent | null = null;

	switch (hookType) {
		case "post-journal":
			event = {
				type: "journal-entry",
				agentId: String(payload.agentId || "unknown"),
				entryPath: String(payload.entryPath || ""),
				entryTitle: payload.entryTitle ? String(payload.entryTitle) : undefined,
				entryContent: String(payload.entryContent || ""),
				entryDate: String(payload.entryDate || new Date().toISOString()),
			};
			break;

		case "post-task-completed":
			event = {
				type: "task-completed",
				agentId: String(payload.agentId || "unknown"),
				taskId: String(payload.taskId || ""),
				taskTitle: String(payload.taskTitle || "Untitled Task"),
				taskDescription: payload.taskDescription
					? String(payload.taskDescription)
					: undefined,
			};
			break;

		case "post-task-created":
			event = {
				type: "task-created",
				agentId: String(payload.agentId || "unknown"),
				taskId: String(payload.taskId || ""),
				taskTitle: String(payload.taskTitle || "Untitled Task"),
				taskDescription: payload.taskDescription
					? String(payload.taskDescription)
					: undefined,
			};
			break;

		case "post-reflection":
			event = {
				type: "reflection-written",
				agentId: String(payload.agentId || "unknown"),
				documentPath: String(payload.documentPath || ""),
				documentTitle: String(payload.documentTitle || "Untitled"),
				reflectionSummary: payload.reflectionSummary
					? String(payload.reflectionSummary)
					: undefined,
			};
			break;

		case "session-start":
			event = {
				type: "session-start",
				agentId: String(payload.agentId || "unknown"),
				sessionId: payload.sessionId ? String(payload.sessionId) : undefined,
			};
			break;

		case "session-end":
			event = {
				type: "session-end",
				agentId: String(payload.agentId || "unknown"),
				sessionId: payload.sessionId ? String(payload.sessionId) : undefined,
			};
			break;
	}

	if (event) {
		await processHookEvent(event, store);
	}
}

