import { QuartzConfig } from "./quartz/cfg"
import * as Plugin from "./quartz/plugins"
import { QuartzPluginData } from "./quartz/plugins/vfile"

// Custom sort: by slug (filename) for chronological HH-MM ordering
// Daily summaries (YYYY-MM-DD.md) sort after atomics
const bySlugChronological = (f1: QuartzPluginData, f2: QuartzPluginData): number => {
  const slug1 = f1.slug ?? ""
  const slug2 = f2.slug ?? ""

  // Extract just the filename from the slug
  const name1 = slug1.split("/").pop() ?? ""
  const name2 = slug2.split("/").pop() ?? ""

  // Daily summaries (YYYY-MM-DD format) should come first
  const isDaily1 = /^\d{4}-\d{2}-\d{2}$/.test(name1)
  const isDaily2 = /^\d{4}-\d{2}-\d{2}$/.test(name2)

  if (isDaily1 && !isDaily2) return -1
  if (!isDaily1 && isDaily2) return 1

  // Otherwise sort by filename (gives HH-MM chronological order)
  return name1.localeCompare(name2)
}

