    // Navigate to next week if requested
    if (options.nextWeek) {
      console.log('Navigating to next week...');
      await page.click('[data-hook="next-arrow"]');
      await page.waitForTimeout(CONFIG.navWaitTime);

      const newWeekInfo = await page.evaluate(() => {
        const text = document.body.innerText;
        const weekMatch = text.match(/Week starting (\w+), (\w+ \d+)/);
        return weekMatch ? weekMatch[0] : null;
      });
      console.log('Now on:', newWeekInfo);
      results.navigatedToWeek = newWeekInfo;
    }

    // Determine which days to fetch
    let daysToFetch = [];

    if (options.days) {
      daysToFetch = options.days;
    } else if (options.weekDays) {
      // Map weekday names to likely day numbers (approximate)
      const today = new Date();
      const dayOfWeek = today.getDay(); // 0 = Sunday
      const currentWeekStart = today.getDate() - dayOfWeek;

      const dayMap = {
        sun: 0, mon: 1, tue: 2, wed: 3, thu: 4, fri: 5, sat: 6
      };

      daysToFetch = options.weekDays.map(d => {
        const offset = dayMap[d.toLowerCase().substring(0, 3)];
        let dayNum = currentWeekStart + offset;
        if (options.nextWeek) dayNum += 7;
        return dayNum;
      });
    } else if (options.day) {
      daysToFetch = [options.day];
    }

    // Fetch each requested day
    for (const dayNum of daysToFetch) {
      console.log(`\nFetching day ${dayNum}...`);

      try {
        await page.click(`text="${dayNum}"`);
        await page.waitForTimeout(CONFIG.dayWaitTime);

        const dayText = await page.evaluate(() => document.body.innerText);
        const dayMatch = dayText.match(/Availability for (\w+), (\w+ \d+)/);

        if (dayMatch) {
          const dayName = dayMatch[1].toLowerCase();
          const datePart = dayMatch[2];

          // Save text
          const textPath = path.join(CACHE_DIR, `ember-${dayName}-${dayNum}.txt`);
          fs.writeFileSync(textPath, dayText);

          // Save screenshot
          const pngPath = path.join(CACHE_DIR, `ember-${dayName}-${dayNum}.png`);
          await page.screenshot({ path: pngPath, fullPage: true });

          // Parse classes
          const classes = parseClassesFromText(dayText);

          results.days[dayNum] = {
            dayName,
            date: datePart,
            textPath,
            pngPath,
            classCount: classes.length,
            classes
          };

          console.log(`  ${dayName} (${datePart}): ${classes.length} classes`);
        }
      } catch (e) {
        console.log(`  Day ${dayNum} failed:`, e.message.substring(0, 50));
        results.days[dayNum] = { error: e.message };
      }
    }

    // If no specific days requested, just save current view
    if (daysToFetch.length === 0) {
      const text = await page.evaluate(() => document.body.innerText);
      const pngPath = path.join(CACHE_DIR, 'ember-schedule.png');
      const textPath = path.join(CACHE_DIR, 'ember-schedule.txt');

      await page.screenshot({ path: pngPath, fullPage: true });
      fs.writeFileSync(textPath, text);

      results.defaultFetch = { pngPath, textPath };
      console.log('Saved default view');
    }

    // Save metadata
    const metaPath = path.join(CACHE_DIR, 'fetch-metadata.json');
    fs.writeFileSync(metaPath, JSON.stringify(results, null, 2));

    return results;

  } catch (error) {
    console.error('Fetch failed:', error.message);
    return { success: false, error: error.message };
  } finally {
    await browser.close();
  }
}

function parseClassesFromText(text) {
  const classes = [];
  const lines = text.split('\n');

  let i = 0;
  while (i < lines.length) {
    const line = lines[i].trim();

    // Match time format: "7:00 a.m." or "5:30 p.m."
    const timeMatch = line.match(/^(\d{1,2}:\d{2})\s*(a\.m\.|p\.m\.)/i);
    if (timeMatch) {
      const hour = parseInt(timeMatch[1].split(':')[0]);
      const minute = timeMatch[1].split(':')[1];
      const isPM = timeMatch[2].toLowerCase().includes('p');
      const hour24 = isPM && hour !== 12 ? hour + 12 : (!isPM && hour === 12 ? 0 : hour);
      const startTime = `${hour24.toString().padStart(2, '0')}:${minute}`;

      // Next non-empty line is class name
      let className = '';
      let j = i + 1;
      while (j < lines.length && !lines[j].trim()) j++;
      if (j < lines.length) className = lines[j].trim();

      // Next non-empty line is instructor
      let instructor = '';
      j++;
      while (j < lines.length && !lines[j].trim()) j++;
      if (j < lines.length) instructor = lines[j].trim();

      // Next non-empty line is duration
      let duration = '';
      j++;
      while (j < lines.length && !lines[j].trim()) j++;
      if (j < lines.length) duration = lines[j].trim();

      if (className && !className.match(/^(Book|Registration)/)) {
        classes.push({
          time: startTime,
          name: className.replace(/[🔥🌤️]/g, '').replace(/\|\s*\$\d+/g, '').trim(),
          instructor,
          duration
        });
      }

      i = j + 1;
      continue;
    }
    i++;
  }

  return classes;
}

// CLI parsing
const args = process.argv.slice(2);
const options = {
  nextWeek: args.includes('--next-week'),
  day: null,
  days: [],
  weekDays: []
};

const dayIdx = args.indexOf('--day');
if (dayIdx !== -1 && args[dayIdx + 1]) {
  options.day = parseInt(args[dayIdx + 1]);
}

const daysIdx = args.indexOf('--days');
if (daysIdx !== -1 && args[daysIdx + 1]) {
  options.days = args[daysIdx + 1].split(',').map(d => parseInt(d));
}

const weekDaysIdx = args.indexOf('--week-days');
if (weekDaysIdx !== -1 && args[weekDaysIdx + 1]) {
  options.weekDays = args[weekDaysIdx + 1].split(',');
}

console.log('Ember Studios Schedule Fetcher');
console.log('Cache dir:', CACHE_DIR);
console.log('Options:', options);
console.log('');

fetchSchedule(options).then(result => {
  if (result.success) {
    console.log('\n✓ Fetch complete!');
    console.log('Results saved to:', CACHE_DIR);

    if (Object.keys(result.days).length > 0) {
      console.log('\nClasses found:');
      for (const [day, info] of Object.entries(result.days)) {
        if (info.classes) {
          console.log(`\n${info.dayName} (day ${day}):`);
          info.classes.forEach(c => {
            console.log(`  ${c.time} ${c.name} (${c.instructor})`);
          });
        }
      }
    }
  } else {
    console.error('\n✗ Fetch failed:', result.error);
    process.exit(1);
  }
});

