/**
 * WeekView component - displays the full weekly calendar grid
 */

import React from "react";
import type { ScheduleBlock, ScheduleConfig, DayOfWeek } from "../../types";
import { DayColumn } from "./DayColumn";

interface WeekViewProps {
  blocks: ScheduleBlock[];
  config: ScheduleConfig;
  onBlockClick?: (block: ScheduleBlock) => void;
}

const DAYS: DayOfWeek[] = [
  "monday",
  "tuesday",
  "wednesday",
  "thursday",
  "friday",
  "saturday",
  "sunday",
];

const HOUR_HEIGHT = 60; // pixels per hour

export function WeekView({ blocks, config, onBlockClick }: WeekViewProps) {
  const { dayStartHour, dayEndHour } = config;
  const totalHours = dayEndHour - dayStartHour;

  // Group blocks by day
  const blocksByDay: Record<DayOfWeek, ScheduleBlock[]> = {
    monday: [],
    tuesday: [],
    wednesday: [],
    thursday: [],
    friday: [],
    saturday: [],
    sunday: [],
  };

  for (const block of blocks) {
    blocksByDay[block.day].push(block);
  }

  // Generate hour labels
  const hourLabels = [];
  for (let hour = dayStartHour; hour < dayEndHour; hour++) {
    const displayHour = hour % 12 || 12;
    const period = hour >= 12 ? "PM" : "AM";
    hourLabels.push(
      <div
        key={hour}
        style={{
          height: `${HOUR_HEIGHT}px`,
          display: "flex",
          alignItems: "flex-start",
          justifyContent: "flex-end",
          paddingRight: "8px",
          paddingTop: "2px",
          fontSize: "11px",
          color: "#6b7280",
          borderBottom: "1px solid transparent",
        }}
      >
        {displayHour} {period}
      </div>
    );
  }

  return (
    <div
      className="week-view"
      style={{
        display: "flex",
        flexDirection: "column",
        height: "100%",
        overflow: "hidden",
      }}
    >
      {/* Scrollable content */}
      <div
        style={{
          flex: 1,
          display: "flex",
          overflow: "auto",
        }}
      >
        {/* Time labels column */}
        <div
          style={{
            width: "60px",
            flexShrink: 0,
            borderRight: "1px solid #e5e7eb",
            backgroundColor: "#f9fafb",
          }}
        >
          {/* Header spacer */}
          <div
            style={{
              position: "sticky",
              top: 0,
              height: "37px",
              backgroundColor: "#f9fafb",
              borderBottom: "1px solid #e5e7eb",
              zIndex: 5,
            }}
          />
          {/* Hour labels */}
          <div>{hourLabels}</div>
        </div>

        {/* Day columns */}
        {DAYS.map((day) => (
          <DayColumn
            key={day}
            day={day}
            blocks={blocksByDay[day]}
            config={config}
            hourHeight={HOUR_HEIGHT}
            onBlockClick={onBlockClick}
          />
        ))}
      </div>
    </div>
  );
}

/**
 * DayColumn component - displays a single day in the weekly grid
 */

import React from "react";
import type { ScheduleBlock, ScheduleConfig, DayOfWeek } from "../../types";
import { TimeBlock } from "./TimeBlock";
import { capitalize } from "../../utils/time";

interface DayColumnProps {
  day: DayOfWeek;
  blocks: ScheduleBlock[];
  config: ScheduleConfig;
  hourHeight: number;
  onBlockClick?: (block: ScheduleBlock) => void;
}

export function DayColumn({
  day,
  blocks,
  config,
  hourHeight,
  onBlockClick,
}: DayColumnProps) {
  const { dayStartHour, dayEndHour } = config;
  const totalHours = dayEndHour - dayStartHour;
  const columnHeight = totalHours * hourHeight;

  // Generate hour grid lines
  const hourLines = [];
  for (let i = 0; i <= totalHours; i++) {
    hourLines.push(
      <div
        key={i}
        style={{
          position: "absolute",
          top: `${i * hourHeight}px`,
          left: 0,
          right: 0,
          height: "1px",
          backgroundColor: i === 0 ? "transparent" : "#e5e7eb",
        }}
      />
    );
  }

  // Check if today
  const today = new Date();
  const dayIndex = ["sunday", "monday", "tuesday", "wednesday", "thursday", "friday", "saturday"];
  const isToday = dayIndex[today.getDay()] === day;

  return (
    <div
      className="day-column"
      style={{
        flex: 1,
        minWidth: "120px",
        borderRight: "1px solid #e5e7eb",
        position: "relative",
      }}
    >
      {/* Day header */}
      <div
        style={{
          position: "sticky",
          top: 0,
          backgroundColor: isToday ? "#dbeafe" : "#f9fafb",
          borderBottom: "1px solid #e5e7eb",
          padding: "8px",
          textAlign: "center",
          fontWeight: 600,
          fontSize: "14px",
          color: isToday ? "#1d4ed8" : "#374151",
          zIndex: 5,
        }}
      >
        {capitalize(day)}
        {isToday && (
          <span
            style={{
              display: "inline-block",
              width: "6px",
              height: "6px",
              backgroundColor: "#1d4ed8",
              borderRadius: "50%",
              marginLeft: "6px",
              verticalAlign: "middle",
            }}
          />
        )}
      </div>

      {/* Time grid */}
      <div
        style={{
          position: "relative",
          height: `${columnHeight}px`,
          backgroundColor: isToday ? "#f0f9ff" : "#fff",
        }}
      >
        {hourLines}

        {/* Blocks */}
        {blocks.map((block) => (
          <TimeBlock
            key={block.id}
            block={block}
            config={config}
            dayStartHour={dayStartHour}
            hourHeight={hourHeight}
            onClick={onBlockClick}
          />
        ))}
      </div>
    </div>
  );
}

